import React from 'react'
import { Box, Stack, Typography, Button } from '@mui/material'
import HeroBannerImage from '../assets/images/banner3.jpg'


const HeroBanner = () => {
  return (
    <Box sx={{
      mt: { lg: '212px', xs: '70px'},
      ml: { sm: '50px'}
    }} position="relative" p="20px">
      <Typography color="#FF2625" fontWeight="600" fontSize="35px">
        BStrong
      </Typography>
      <Typography  fontWeight="600" 
      sx={{ fontSize:{ lg: "30px", xs: '35px' }}} mb="23px" mt="30px" >
        Sweat, Build <br /> and Repeat
      </Typography>
      <Typography color="#212121" fontSize="25px" lineHeight="35px" mb={4}>
        Checkout the most effective exercises
      </Typography>
      <Button variant="contained" color="error" href="#exercises" sx={{ backgroundColor: '#ff2625', padding: '10px'}}>Explore Exercises</Button>
      <Typography
      fontWeight={600}
      color="#ff2625"
      sx={{
        opacity: 0.2,
        display: { lg: 'block', xs: 'none'}
      }}
      fontSize="200px"
      pt="40px"
      >
        Exercise
      </Typography>
      <img
        src={HeroBannerImage}
        alt="banner"
        className='hero-banner-img'
        style={{ width: '100%', maxWidth: '400px', margin: '0 auto' }}
      />


    </Box>
  )
}

export default HeroBanner
