import React, { useState } from 'react';
import { Route, Routes, Navigate } from 'react-router-dom';
import { Box } from '@mui/material';
import './App.css';
import Navbar from './components/Navbar';
import Home from './pages/Home';
import ExerciseDetail from './pages/ExerciseDetail';
import Footer from './components/Footer';
import Signup from './components/Signup';
import Login from './components/Login';
import HeroBanner from './components/HeroBanner';

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false); // Add a state variable to track login status

  // Function to set login status when a successful login occurs
  const handleLogin = () => {
    setIsLoggedIn(true);
  };

  return (
    <Box width="400px" sx={{ width: { xl: '1488px' } }} m="auto">
      <Navbar />
      <Routes>
        <Route
          path="/"
          element={isLoggedIn ? <Navigate to="/Home" /> : <HeroBanner handleLogin={handleLogin} />}
        />
        <Route path="/exercise/:id" element={<ExerciseDetail />} />
        <Route path="/signup" element={<Signup />} />
        <Route path="/login" element={<Login handleLogin={handleLogin} />} />
        
      </Routes>
      <Footer />
    </Box>
  );
}

export default App;
