import React, { useState } from 'react';
import { Box, Typography, Button, Dialog, DialogContent } from '@mui/material';
import HeroBannerImage from '../assets/images/banner3.jpg';

const HeroBanner = () => {
  const [isDialogOpen, setDialogOpen] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false); // Add a state variable to track login status

  const openDialog = () => {
    setDialogOpen(true);
  };

  const closeDialog = () => {
    setDialogOpen(false);
  };

  // Simulate a user login (you can replace this with your actual login logic)
  const handleLogin = () => {
    // Perform the login logic here
    // Once the user is logged in successfully, set isLoggedIn to true
    setIsLoggedIn(true);
    closeDialog(); // Close the login dialog
  };

  return (
    <Box
      sx={{
        mt: { lg: '212px', xs: '70px' },
        ml: { sm: '50px' },
      }}
      position="relative"
      p="10px"
    >
      <Typography color="#FF2625" fontWeight="600" fontSize="35px">
        BStrong
      </Typography>
      <Typography
        fontWeight="600"
        sx={{ fontSize: { lg: '30px', xs: '35px' } }}
        mb="23px"
        mt="30px"
      >
        Sweat, Build <br /> and Repeat
      </Typography>
      <Typography color="#212121" fontSize="25px" lineHeight="35px" mb={4}>
        Checkout the most effective exercises
      </Typography>
      <Button
        variant="contained"
        color="error"
        onClick={isLoggedIn ? undefined : openDialog} // Open the dialog only if not logged in
        sx={{ backgroundColor: '#ff2625', padding: '10px' }}
      >
        Explore Exercises
      </Button>
      <Typography
        fontWeight={600}
        color="#ff2625"
        sx={{
          opacity: 0.2,
          display: { lg: 'block', xs: 'none' },
        }}
        fontSize="200px"
        pt="40px"
      >
        Exercise
      </Typography>
      <img
        src={HeroBannerImage}
        alt="banner"
        className="hero-banner-img"
        style={{
          width: '80%',
          maxWidth: '40%',
          maxHeight: '700px',
          margin: '1px auto',
        }}
      />

      {/* Login dialog */}
      <Dialog open={isDialogOpen} onClose={closeDialog}>
        <DialogContent>
          <Typography variant="h5" mb={2}>
            Please login to explore the exercises.
          </Typography>
          <Button
            variant="contained"
            color="error"
            onClick={handleLogin} // Trigger the login action
            sx={{ backgroundColor: '#ff2625', padding: '10px' }}
          >
            Login
          </Button>
        </DialogContent>
      </Dialog>
    </Box>
  );
};

export default HeroBanner;
